// ProductList.js (Parent Component)
import React, { useState } from 'react';
import Product from './Product';
import Main from './Main'; // Import Main component

const ProductList = ({ products }) => {
    const [selectedCategory, setSelectedCategory] = useState(null);

    const handleCategorySelect = (category) => {
        setSelectedCategory(category === selectedCategory ? null : category);
    };

    // Filter products based on selected category
    const filteredProducts = selectedCategory
        ? products.filter(product => product.category === selectedCategory)
        : [];

    return (
        <div>
            <div>
                <button onClick={() => handleCategorySelect(null)}>All Categories</button>
                <button onClick={() => handleCategorySelect('dairy')}>Dairy</button>
                <button onClick={() => handleCategorySelect('fruit')}>Fruit</button>
                {/* Add more buttons for other categories */}
            </div>
            <div>
                {filteredProducts.length > 0 ? (
                    <div>
                        {filteredProducts.map(product => (
                            <Product
                                key={product.id}
                                url={product.url}
                                name={product.name}
                                description={product.description}
                                category={product.category}
                                price={product.price}
                                rating={product.rating}
                            />
                        ))}
                    </div>
                ) : (
                    selectedCategory ? <p>No products available for the selected category</p> : null
                )}
            </div>
            <Main products={products} /> {/* Pass products to Main component */}
        </div>
    );
};

export default ProductList;
