// ProductList.js (Parent Component)
import React, { useState } from 'react';
import Product from './Product';
import productsData from './ProductData';

const ProductList = () => {
    const [selectedType, setSelectedType] = useState(null);

    const handleTypeSelect = (type) => {
        setSelectedType(type === selectedType ? null : type);
    };

    // Filter products based on selected type
    const filteredProducts = selectedType
        ? productsData.filter(product => product.type === selectedType)
        : productsData;

    return (
        <div>
            <div>
                <button onClick={() => handleTypeSelect(null)}>All Types</button>
                <button onClick={() => handleTypeSelect('Type A')}>Dairy</button>
                <button onClick={() => handleTypeSelect('Type B')}>Type B</button>
                {/* Add more buttons for other types */}
            </div>
            <div>
                {filteredProducts.map(product => (
                    <Product
                        key={product.id}
                        url={product.url}
                        name={product.name}
                        description={product.description}
                        type={product.type}
                        price={product.price}
                        rating={product.rating}
                    />
                ))}
            </div>
        </div>
    );
};

export default ProductList;
