import React from 'react';

const Product = ({ image, name, description, type, price, rating }) => {
    return (
        <div className="product">
            <img src={image} alt={name} />
            <h3>{name}</h3>
            <p>{description}</p>
            <p>Type: {type}</p>
            <p>Price: {price}</p>
            <p>Rating: {rating}</p>
            <div className="product-buttons">
                <button>LIKE</button>
                <button>SHARE</button>
                <button>PURCHASE</button>
            </div>
        </div>
    );
};

export default Product;
