import React, { useState } from 'react';
import '../css/Navbar.css'

const Navbar = ({ onCategorySelect }) => {
    const [selectedCategory, setSelectedCategory] = useState(null);

    const handleCategorySelect = (category) => {
        setSelectedCategory(category);
        onCategorySelect(category);
    };

    return (
        <nav className="navbar">
            <div className="navbar-brand">
               Hope
            </div>
            <div className="category-buttons">
                <button className={`category-button ${selectedCategory === null && 'active'}`} onClick={() => handleCategorySelect(null)}>All Categories</button>
                <button className={`category-button ${selectedCategory === 'dairy' && 'active'}`} onClick={() => handleCategorySelect('dairy')}>Dairy</button>
                <button className={`category-button ${selectedCategory === 'fruit' && 'active'}`} onClick={() => handleCategorySelect('fruit')}>Fruit</button>
                <button className={`category-button ${selectedCategory === 'vegetables' && 'active'}`} onClick={() => handleCategorySelect('vegetables')}>Vegetables</button>
            </div>
        </nav>
    );
};

export default Navbar;
